/* eslint-disable react/style-prop-object */
import React, { useState, useEffect } from 'react';
import ReactMapboxGl, { Layer, Feature } from 'react-mapbox-gl';
import { useDispatch, useSelector } from 'react-redux';

import {
	selectDataCorona,
	selectIsFetching
} from '../../redux/data/data.selectors';
import { selectToggleNumber } from '../../redux/toggle-hidden/toggle-hidden.selectors';

import { fetchDataStartAsync } from '../../redux/data/data.actions';
import { toggleNumber } from '../../redux/toggle-hidden/toggle-hidden.actions';

import Mark from '../mark/mark.component';

import { CircleContainer } from '../mark/mark.style';

const MapGl = ReactMapboxGl({
	accessToken: process.env.REACT_APP_MAPBOX_TOKEN
});

const Map = () => {
	const [viewPort, setViewPort] = useState({
		latitude: 37.8,
		longitude: -122.4,

		width: '100vw',
		height: '100vh'
	});
	const dataCorona = useSelector(selectDataCorona);
	const onToggleNumber = useSelector(selectToggleNumber);

	const isFetching = useSelector(selectIsFetching);
	const dispatch = useDispatch();
	console.log(isFetching);
	useEffect(() => {
		dispatch(fetchDataStartAsync());
	}, [dispatch, isFetching]);
	return (
		<div>
			<MapGl
				style='mapbox://styles/boyperfer/ck8c3jmqk2mjy1iqitxwkd1b6'
				containerStyle={{
					height: '100vh',
					width: '100vw',
					latitude: 37.8,
					longitude: -122.4
				}}
				zoom={[1]}
			/>
			{/* {isFetching ? (
				<div></div>
			) : (
				dataCorona.map(({ coordinate }) => (
					<Layer type='sympol'>{console.log(coordinate)}</Layer>
				))
			)} */}
		</div>
	);
};

export default Map;
