import React from 'react';

import { useSelector } from 'react-redux';

import {
	selectDataObjectCorona,
	selectIsFetching,
	selectIsFetchingObject,
} from '../../redux/data/data.selectors';
import { selectHome } from '../../redux/myhome/myhome.selectors';

import { numberWithCommas } from '../../assets/utils/utils';

import {
	WrapContainer,
	NameContainer,
	NumberContainer,
	TableContainer,
} from './detail.styles';

const Detail = () => {
	const dataObject = useSelector(selectDataObjectCorona);
	const home = useSelector(selectHome);
	const isFetchingObject = useSelector(selectIsFetchingObject);
	const country = isFetching ? null : dataObject[home];
	console.log(dataObject[home]);
	return isFetching ? (
		<div />
	) : (
		<TableContainer>
			<WrapContainer>
				<NumberContainer>
					<WrapContainer one>
						<NameContainer> Ca Nhiễm </NameContainer>
						<NumberContainer confirmed>
							{' '}
							{/* {numberWithCommas(country.confirmed)}{' '} */}
						</NumberContainer>
					</WrapContainer>
					<WrapContainer two>
						<NameContainer recovered> Ca Phục Hồi </NameContainer>
						<NumberContainer recovered>
							{' '}
							{/* {numberWithCommas(country.recovered)}{' '} */}
						</NumberContainer>
					</WrapContainer>
					<WrapContainer three last>
						<NameContainer deaths> Ca Tử Vong </NameContainer>
						<NumberContainer deaths>
							{' '}
							{/* {numberWithCommas(country.deaths)}{' '} */}
						</NumberContainer>
					</WrapContainer>
				</NumberContainer>
			</WrapContainer>
		</TableContainer>
	);
};

export default Detail;
