import { keyframes } from 'styled-components';

export const fadeInUp = keyframes`
	from {
        transform: translate3d(0,40px,0)
    }

    to {
        transform: translate3d(0,0,0);
        opacity: 1
    }
`;

export const fadeInDown = keyframes`
	from {
    opacity: 1;
	}

	to {
    opacity: 0;
    transform: translate3d(0, 100%, 0);
	}
`;
