export const clickCountry = (poppedCountry, countryToAdd) => {
	const checkCountryEmpty = poppedCountry => {
		return poppedCountry === undefined || poppedCountry.length === 0;
	};

	if (checkCountryEmpty(poppedCountry)) {
		return [countryToAdd];
	}

	return [];
};
