import React from "react";
import { useSelector } from "react-redux";

import { selectCountry } from "../../redux/myhome/myhome.selectors";
import {
    selectDataObjectCorona,
    selectIsFetchingObject,
} from "../../redux/data/data.selectors";

import healthImage from "../../assets/icon/image-health.png";

import {
    DetailOthersContainer,
    DeathsContainer,
    NameContainer,
    ConfirmedContainer,
    DetailOthersWrap,
    RecoveredContainer,
    NumberContainer,
    CasesContainer,
    Wrapper,
    RecoveredAndDeaths,
    NameRecoveredDeaths,
    NumberRecoveredDeaths,
    ImgContainer,
} from "./detail-others.style";

const DetailOthers = () => {
    const chosenCountry = useSelector(selectCountry);
    console.log(healthImage);
    const dataObjectCorona = useSelector(selectDataObjectCorona);
    const isFetchingObject = useSelector(selectIsFetchingObject);
    const country = isFetchingObject
        ? {}
        : dataObjectCorona[chosenCountry[0].toLowerCase().replace(/\s+/g, "")];
    const { deaths, confirmed, recovered, name, code } = country;
    return (
        <DetailOthersContainer>
            <DetailOthersWrap>
                <NameContainer>
                    {name}
                    <ImgContainer
                        src={`https://lipis.github.io/flag-icon-css/flags/4x3/${code}.svg`}
                    />
                </NameContainer>
                <Wrapper>
                    <ConfirmedContainer>
                        <CasesContainer>Cases </CasesContainer>
                        <NumberContainer>{confirmed}</NumberContainer>
                    </ConfirmedContainer>
                    <RecoveredAndDeaths>
                        <RecoveredContainer>
                            <NameRecoveredDeaths
                                scr={`https://www.google.com/url?sa=i&url=https%3A%2F%2Fsites.psu.edu%2Fsiowfa16%2F2016%2F10%2F20%2Fsmile%2F&psig=AOvVaw1ShPPgKa_XOQw55G0SCtat&ust=1611030082603000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCKDR5fDQpO4CFQAAAAAdAAAAABAD`}
                                alt={1}
                            />
                            <NumberRecoveredDeaths>
                                {recovered}
                            </NumberRecoveredDeaths>
                        </RecoveredContainer>
                        <DeathsContainer>
                            <NameRecoveredDeaths>Deaths: </NameRecoveredDeaths>
                            <NumberRecoveredDeaths>
                                {deaths}
                            </NumberRecoveredDeaths>
                        </DeathsContainer>
                    </RecoveredAndDeaths>
                </Wrapper>
            </DetailOthersWrap>
        </DetailOthersContainer>
    );
};

export default DetailOthers;
