import styled from 'styled-components';

import { Popup } from 'react-mapbox-gl';

export const PopupContainer = styled(Popup)`
	display: flex;
`;

export const NameContainer = styled.div`
	font-size: 3rem;
	font-weight: bold;
	padding: 1rem;
	margin: 1rem;
`;

export const ConfirmedContainer = styled.div`
	font-size: 2rem;
	padding: 1rem;
	margin: 1rem;
`;
