import OthersActionTypes from "./others.types";

const INITIAL_STATE = {
    onOthers: false,
};

const othersReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case OthersActionTypes:
            return {
                ...state,
                onOthers: !state.onOthers,
            };
        default:
            return state;
    }
};

export default othersReducer;
