import styled from 'styled-components';

import mainStyle from '../../styles/main';

const {
	variables: { primary, greyDark3, white },
} = mainStyle;

export const HomeContainer = styled.span`
	display: flex;
	flex-direction: column;
	width: 40vw;
	height: 100vh;
	background-color: ${greyDark3};
	line-height: normal;
`;

export const CountryContainer = styled.span`
	font-size: 4rem;
	align-self: center;
	color: ${white};
`;

export const NameContainer = styled.span`
	font-size: 3rem;
	font-weight: 500
	color: ${white}
`;

export const NumberContainer = styled.span`
	font-size: 8rem;
	color: ${primary};
	font-weight: 700;
	border: 1px solid #bdbdbd;
`;
