export const existingCountry = (poppedCountry, countryToAdd) => {
	if (poppedCountry !== undefined || poppedCountry.length !== 0) {
		return poppedCountry[0].name === countryToAdd.name
			? []
			: [countryToAdd];
	}
	return [];
};
