import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { fetchDataStartAsync } from './redux/data/data.actions';
import { selectDisplayHome } from './redux/myhome/myhome.selectors';

import { mobileAndTabletcheck } from './assets/utils/utils';

import NavigationButton from './component/navitation-button/navigation-button.component';

import { AppContainer, MapContainer, MyHomeContainer } from './App.style';
import { GlobalStyles } from './styles/global-style';

const App = () => {
	const dispatch = useDispatch();
	const displayHome = useSelector(selectDisplayHome);
	useEffect(() => {
		dispatch(fetchDataStartAsync());
	}, [dispatch]);
	const isMobile = mobileAndTabletcheck();
	console.log(isMobile, displayHome);
	return (
		<AppContainer>
			<GlobalStyles />
			{/* {displayHome ? <MyHomeContainer /> : null} */}
			<MapContainer />
			{isMobile ? <NavigationButton /> : null}
		</AppContainer>
	);
};

export default App;
