import MyHomeActionTypes from './myhome.types';

import { mobileAndTabletcheck } from '../../assets/utils/utils';

const isMobile = mobileAndTabletcheck();

const INITIAL_STATE = {
	home: ['vietnam'],
	displayHome: isMobile ? false : true,
};

const myHomeReducer = (state = INITIAL_STATE, action) => {
	switch (action.type) {
		case MyHomeActionTypes.CHANGE_MYHOME:
			return {
				...state,
				home: [action.payload],
			};
		case MyHomeActionTypes.TOGGLE_MYHOME:
			return {
				...state,
				displayHome: !state.displayHome,
			};
		default:
			return state;
	}
};

export default myHomeReducer;
