import styled from 'styled-components';

import mainStyle from '../../styles/main';

const {
	variables: { primary, primaryLight, white },
} = mainStyle;

export const HomeContainer = styled.span`
	float: left;
	width: 250px;
	background: rgba(255, 255, 255, 1);
	text-align: center;
`;

export const CountryContainer = styled.span`
	height: 100px;
	width: 100px;
	text-align: center;
	background-color: ${primaryLight};
	border-radius: 50%;
	line-height: 100px;
	color: #fff;
	font-size: 3rem;
	font-weight: 100;
	margin: 20px auto;
`;

export const NameContainer = styled.p`
	font-size: 3rem;
	font-weight: 500
	color: ${white}
`;

export const NumberContainer = styled.span`
	font-size: 8rem;
	color: ${primary};
	font-weight: 700;
	border: 1px solid #bdbdbd;
`;

export const TitleContainer = styled.h1`
	margin: 20px 0 10px 0;
	font-size: 1.25em;
	color: rgba(0, 0, 0, 0.8);
`;
