import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import ReactMapGL, { Layer } from 'react-map-gl';

import {
	selectDataCorona,
	selectIsFetching
} from '../../redux/data/data.selectors';
import { selectToggleNumber } from '../../redux/toggle-hidden/toggle-hidden.selectors';

import { toggleNumber } from '../../redux/toggle-hidden/toggle-hidden.actions';
import { fetchDataStartAsync } from '../../redux/data/data.actions';

import { NumberContainer, CircleContainer } from './mark.style';

const Mark = () => {
	const dataCorona = useSelector(selectDataCorona);
	const onToggleNumber = useSelector(selectToggleNumber);
	const isFetching = useSelector(selectIsFetching);
	const dispatch = useDispatch();
	console.log(dataCorona.coordinates);
	useEffect(() => {
		dispatch(fetchDataStartAsync());
	}, [dispatch]);
	return isFetching ? (
		<div></div>
	) : (
		dataCorona.map(({ coordinates, confirmed }, i) => (
			<Layer key={i} latitude={coordinates[0]} longitude={coordinates[1]}>
				<NumberContainer onToggleNumber={onToggleNumber}>
					{confirmed}
					{console.log(12)}
				</NumberContainer>
				<CircleContainer onClick={() => dispatch(toggleNumber())} />
			</Layer>
		))
	);
};

export default Mark;
