import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';

import { fetchDataStartAsync } from './redux/data/data.actions';

import { mobileAndTabletcheck } from './assets/utils/utils';

import NavigationButton from './component/navitation-button/navigation-button.component';
import WithLoader from './component/with-loader/with-loader.component';

import { AppContainer, MapContainer, MyHomeContainer } from './App.style';
import { GlobalStyles } from './styles/global-style';
import { selectIsFetching } from './redux/data/data.selectors';

const MapLoader = WithLoader(MapContainer);

const App = () => {
	const mobile = mobileAndTabletcheck();
	const isFetching = useSelector(selectIsFetching);
	const dispatch = useDispatch();
	useEffect(() => {
		dispatch(fetchDataStartAsync());
	}, [dispatch]);
	return (
		<AppContainer>
			<GlobalStyles />
			<MyHomeContainer />
			<MapLoader isFetching={isFetching} />
			{mobile ? <NavigationButton /> : null}
		</AppContainer>
	);
};

export default App;
