import styled from 'styled-components';

import mainStyle from '../../styles/main';

const {
	variables: { primary },
} = mainStyle;

export const HomeContainer = styled.span`
	display: flex;
	flex-direction: column;
	width: 40vw;
	height: 100vh;
`;

export const CountryContainer = styled.span`
	font-size: 4rem;
	align-self: center;
`;

export const NameContainer = styled.span`
	font-size: 3rem;
`;

export const NumberContainer = styled.span`
	font-size: 8rem;
	color: ${primary};
`;
