import styled from 'styled-components';

import mainStyle from '../../styles/main';

const {
	variables: { primary, primaryLight, white, black, greyDark1, testWhite },
} = mainStyle;

export const HomeContainer = styled.span`
	float: left;
	width: 250px;
	background: ${greyDark1};
	text-align: center;
`;

export const CountryContainer = styled.div`
	height: 100px;
	width: 100px;
	text-align: center;
	background-color: rgba(231, 76, 60, 1);
	border-radius: 50%;
	line-height: 100px;
	color: #fff;
	font-size: 1.5em;
	font-weight: 100;
	margin: 20px auto;
`;

export const NameContainer = styled.p`
	color: ${testWhite};
	margin: 10px 0;
	font-weight: 500;
	font-size: 3rem;
`;

export const NumberContainer = styled.span`
	margin-bottom: 20px;
	padding-bottom: 10px;
	display: inline-block;
	width: 125px;
	font-size: 10rem;
	font-weight: 700;
	letter-spacing: 1px;
	color: ${primary};
	border-bottom: 1px solid rgba(0, 0, 0, 0.1);
`;

export const TitleContainer = styled.h1`
	margin: 20px 0 10px 0;
	font-size: 1.25em;
	color: rgba(0, 0, 0, 0.8);
`;
