import React, { useState, useEffect } from 'react';
import ReactMapGL, { Layer, Source } from 'react-map-gl';
import { useDispatch, useSelector } from 'react-redux';

import { selectDataCorona } from '../../redux/data/data.selectors';
import { selectToggleNumber } from '../../redux/toggle-hidden/toggle-hidden.selectors';

import { fetchDataStartAsync } from '../../redux/data/data.actions';
import { toggleNumber } from '../../redux/toggle-hidden/toggle-hidden.actions';

import Mark from '../mark/mark.component';

import { CircleContainer } from '../mark/mark.style';

const Map = () => {
	const [viewPort, setViewPort] = useState({
		latitude: 37.8,
		longitude: -122.4,
		zoom: 2,
		width: '100vw',
		height: '100vh'
	});
	const dataCorona = useSelector(selectDataCorona);
	const onToggleNumber = useSelector(selectToggleNumber);
	// const isFetching = useSelector(selectIsFetching);
	const dispatch = useDispatch();
	useEffect(() => {
		dispatch(fetchDataStartAsync());
	}, [dispatch]);
	return (
		<ReactMapGL
			{...viewPort}
			onViewportChange={setViewPort}
			mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_TOKEN}
			mapStyle='mapbox://styles/boyperfer/ck8c3jmqk2mjy1iqitxwkd1b6'
		>
			{/* <Mark /> */}
			{dataCorona.map(({ coordinates, confirmed }, i) => (
				<Source type='geojson'>
					<Layer
						type='symbol'
						id='marker'
						layout={{ 'icon-image': 'marker-15' }}
					>
						<div onToggleNumber={onToggleNumber}>{confirmed}</div>
						<Feature
							coordinates={[coordinates[0], coordinates[1]]}
						/>
						<CircleContainer
							onClick={() => dispatch(toggleNumber())}
						/>
					</Layer>
				</Source>
			))}
		</ReactMapGL>
	);
};

export default Map;
