import React from "react";
import { useSelector, useDispatch } from "react-redux";

import {
    selectIsFetching,
    selectDataCorona,
} from "../../redux/data/data.selectors";
import {
    selectToggleHome,
    selectDisplayHome,
    selectHome,
    selectToggleBackgroundHome,
} from "../../redux/myhome/myhome.selectors";
import { changeMyHome } from "../../redux/myhome/myhome.actions";

import { mobileAndTabletcheck } from "../../assets/utils/utils";

import Filter from "../other-countries/other-countries.component";
import Detail from "../detail/detail.component";

import {
    HomeContainer,
    DetailContainer,
    FlagContainer,
    NavigationHomeContainer,
    DateContainer,
    OverallContainer,
    FilterContainer,
} from "./myhome.styles";

const MyHome = () => {
    const dispatch = useDispatch();
    const isFetching = useSelector(selectIsFetching);
    const isOnHome = useSelector(selectToggleHome);
    const displayHome = useSelector(selectDisplayHome);
    const mobile = mobileAndTabletcheck();
    const home = useSelector(selectHome);
    const isOthers = useSelector(selectToggleBackgroundHome);
    console.log(isOthers[0] === "others");

    return isFetching ? (
        <div />
    ) : mobile ? (
        displayHome ? (
            <div>
                <OverallContainer>
                    <HomeContainer isOnHome={isOnHome}>
                        <FlagContainer />
                        <div>
                            {isOthers[0] === "others" ? (
                                <FilterContainer />
                            ) : (
                                <DetailContainer />
                            )}
                        </div>
                        <NavigationHomeContainer />
                        <DateContainer />
                    </HomeContainer>
                </OverallContainer>
            </div>
        ) : null
    ) : (
        <HomeContainer>
            <FlagContainer />
            <div>
                {isOthers[0] === "others" ? (
                    <FilterContainer />
                ) : (
                    <DetailContainer />
                )}
            </div>
            <NavigationHomeContainer />
            <DateContainer />
        </HomeContainer>
        // <Filter />
    );
};

export default MyHome;
