import React from 'react';
import { useSelector, useDispatch } from 'react-redux';

import { changeMyHome } from '../../redux/myhome/myhome.actions';

import {
	selectDataCorona,
	selectIsFetching,
} from '../../redux/data/data.selectors';

import { selectHome } from '../../redux/myhome/myhome.selectors';

import { numberWithCommas, getDate } from '../../assets/utils/utils';

import {
	HomeContainer,
	NameContainer,
	NumberContainer,
	TableContainer,
	CountryContainer,
	WrapContainer,
	ImgContainer,
	NameCountryContainer,
	NavContainer,
	LinkContainer,
	TimeContainer,
	DateContainer,
	TitleContainer,
} from './myhome.styles';

const MyHome = () => {
	const dataCorona = useSelector(selectDataCorona);
	const isFetching = useSelector(selectIsFetching);
	const home = useSelector(selectHome);
	console.log(home[0]);
	const dispatch = useDispatch();
	const countriesHarsh = dataCorona.reduce((map, obj) => {
		map[obj.name.replace(/\s+/g, '').toLowerCase()] = { ...obj };
		return map;
	}, {});
	const country = countriesHarsh['vietnam'];
	const us = countriesHarsh['us'];
	const date = isFetching ? null : getDate(us.lastUpdate);
	return isFetching ? (
		<div />
	) : (
		<HomeContainer>
			<CountryContainer>
				<NameCountryContainer>{us.name}</NameCountryContainer>
				<ImgContainer
					src={`https://lipis.github.io/flag-icon-css/flags/4x3/${us.code}.svg`}
				/>
			</CountryContainer>
			<TableContainer>
				<WrapContainer one>
					<NameContainer> Ca Nhiễm </NameContainer>
					<NumberContainer confirmed>
						{' '}
						{numberWithCommas(us.confirmed)}{' '}
					</NumberContainer>
				</WrapContainer>
				<WrapContainer two>
					<NameContainer recovered> Ca Phục Hồi </NameContainer>
					<NumberContainer recovered>
						{' '}
						{numberWithCommas(us.recovered)}{' '}
					</NumberContainer>
				</WrapContainer>
				<WrapContainer three last>
					<NameContainer deaths> Ca Tử Vong </NameContainer>
					<NumberContainer deaths>
						{' '}
						{numberWithCommas(us.deaths)}{' '}
					</NumberContainer>
				</WrapContainer>
			</TableContainer>
			<NavContainer>
				<LinkContainer
					onClick={() => dispatch(changeMyHome('vietnam'))}
				>
					VN
				</LinkContainer>
				<LinkContainer onClick={() => dispatch(changeMyHome('us'))}>
					US
				</LinkContainer>
				<LinkContainer>Others</LinkContainer>
			</NavContainer>
			<DateContainer>
				<TitleContainer>Ngày cuối cập nhật (M/D/YYYY)</TitleContainer>
				<TimeContainer>{date}</TimeContainer>
			</DateContainer>
		</HomeContainer>
	);
};

export default MyHome;
