import React from 'react';
import { useSelector, useDispatch } from 'react-redux';

import { changeMyHome } from '../../redux/myhome/myhome.actions';

import {
	selectDataCorona,
	selectIsFetching,
} from '../../redux/data/data.selectors';

import { selectHome } from '../../redux/myhome/myhome.selectors';

import { getDate } from '../../assets/utils/utils';

import Detail from '../detail/detail.component';
import Flag from '../flag/flag.component';
import NavigationHome from '../navigation-home/navigation-home.component';
import Date from '../date/date.component';

import {
	HomeContainer,
	NavContainer,
	TimeContainer,
	DateContainer,
	TitleContainer,
} from './myhome.styles';

const MyHome = () => {
	const dataCorona = useSelector(selectDataCorona);
	const isFetching = useSelector(selectIsFetching);
	const home = useSelector(selectHome);
	const countriesHarsh = dataCorona.reduce((map, obj) => {
		map[obj.name.replace(/\s+/g, '').toLowerCase()] = { ...obj };
		return map;
	}, {});
	const country = countriesHarsh[home[0]];
	const date = isFetching ? null : getDate(country.lastUpdate);
	return isFetching ? (
		<div />
	) : (
		<HomeContainer>
			<Flag />
			<Detail />
			<NavigationHome />
			<Date />
		</HomeContainer>
	);
};

export default MyHome;
