import React from 'react';
import { Popup } from 'react-mapbox-gl';
import { useSelector } from 'react-redux';

import { selectPopupCountries } from '../../redux/popup/popup.selectors';

import './popup.styles.scss';
import {
	NumberContainer,
	NameContainer,
	CountryContainer
} from './popup.styles';

import CodeCountry from '../../assets/countryflag.json';

const PopupNumber = () => {
	const popupCountries = useSelector(selectPopupCountries);
	const result = CodeCountry.reduce((map, obj) => {
		map[obj.Name] = obj.Code;
		return map;
	}, {});
	const { confirmed, deaths, recovered, name, coordinates } = popupCountries;
	const getCode = result[name];
	return (
		<Popup coordinates={[coordinates[1], coordinates[0]]}>
			<NameContainer>
				{name}{' '}
				{typeof getCode !== 'undefined' ? (
					<CountryContainer
						src={`https://lipis.github.io/flag-icon-css/flags/4x3/${getCode.toLowerCase()}.svg`}
					/>
				) : (
					<div></div>
				)}
			</NameContainer>
			<NumberContainer> {confirmed} confirmed </NumberContainer>
			<NumberContainer> {deaths} deaths </NumberContainer>
			<NumberContainer> {recovered} recovered </NumberContainer>
		</Popup>
	);
};

export default PopupNumber;
