import styled from "styled-components";

import mainStyles from "../../styles/main";
import { primary } from "../../styles/variables";

const {
    variables: { greyDark1, greyDark4, textWhite, primaryDark },
    device: { mobile },
} = mainStyles;

export const TableContainer = styled.div`
    display: flex;
    flex-direction: column;
    background-color: black;
    height: 100%;
    overflow: auto;
    overscroll-behavior-y: auto;
`;

export const SearchFieldWrap = styled.div`
    background-color: black;
    margin-bottom: 1rem;
    padding-right: 1rem;
`;

export const SearchFieldContainer = styled.input`
    background-color: ${greyDark1};
    color: ${textWhite};
    width: 100%;
    font-size: 2.5rem;
    border: 1px solid black;
    padding-right: 1rem;
`;

export const WrapContainer = styled.div`
    background-color: black;
    border: 1px solid black;
    padding-right: 1rem;
    border-bottom: none;

    @media ${mobile} {
        height: 50vh;
    }
`;

export const CountryContainer = styled.div`
    border: 1px solid black;
    background-color: ${greyDark4};
    font-size: 2.5rem;
    color: ${textWhite};
    border-bottom: 2px solid ${greyDark1};
    padding: 0.5rem;
`;

export const CasesContainer = styled.div`
    color: ${primary};
`;
