import React from "react";
import { useSelector, useDispatch } from "react-redux";

import {
    selectIsFetching,
    selectDataCorona,
} from "../../redux/data/data.selectors";
import {
    selectToggleHome,
    selectDisplayHome,
    selectHome,
} from "../../redux/myhome/myhome.selectors";
import { changeMyHome } from "../../redux/myhome/myhome.actions";

import { mobileAndTabletcheck } from "../../assets/utils/utils";

import Filter from "../other-countries/other-countries.component";

import {
    HomeContainer,
    DetailContainer,
    FlagContainer,
    NavigationHomeContainer,
    DateContainer,
    OverallContainer,
} from "./myhome.styles";

const MyHome = () => {
    const dispatch = useDispatch();
    const isFetching = useSelector(selectIsFetching);
    const isOnHome = useSelector(selectToggleHome);
    const displayHome = useSelector(selectDisplayHome);
    const mobile = mobileAndTabletcheck();
    const home = useSelector(selectHome);

    return isFetching ? (
        <div />
    ) : mobile ? (
        displayHome ? (
            <div>
                <OverallContainer>
                    <HomeContainer isOnHome={isOnHome}>
                        <FlagContainer />
                        <DetailContainer>
                            <Detail />
                        </DetailContainer>
                        <NavigationHomeContainer />
                        <DateContainer />
                    </HomeContainer>
                </OverallContainer>
            </div>
        ) : null
    ) : (
        <HomeContainer>
            <FlagContainer />
            <DetailContainer />
            <NavigationHomeContainer />
            <DateContainer />
        </HomeContainer>
        // <Filter />
    );
};

export default MyHome;
