import React, { useState } from 'react';
import MapGL from 'mapbox-gl';

const MAPBOX_TOKEN =
	'pk.eyJ1IjoiYm95cGVyZmVyIiwiYSI6ImNrOGF5aGRybDAzOGcza293NXI0OGNodTAifQ.k1EdkowgT451b0uqW-kQHw';

const Root = () => {
	const [viewPort, setViewPort] = useState({
		latitude: 37.8,
		longitude: -122.4,
		zoom: 14,
		bearing: 0,
		pitch: 0
	});

	return (
		<MapGL
			{...viewPort}
			width='100vw'
			height='100vh'
			mapStyle='mapbox://styles/mapbox/dark-v9'
			onViewportChange={setViewport}
			mapboxApiAccessToken={MAPBOX_TOKEN}
		/>
	);
};

export default Root;
