import React from "react";
import { useSelector, useDispatch } from "react-redux";

import { selectCountries } from "../../redux/myhome/myhome.selectors";
import { filterCountries } from "../../redux/myhome/myhome.actions";
import {
    selectIsFetching,
    selectDataCorona,
} from "../../redux/data/data.selectors";

import { numberWithCommas } from "../../assets/utils/utils";

import {
    TableContainer,
    CountryContainer,
    WrapContainer,
    SearchFieldContainer,
    SearchFieldWrap,
    CasesContainer,
    NameContainer,
} from "./other-countries.style";

const OtherCountries = () => {
    const dispatch = useDispatch();
    const countries = useSelector(selectCountries);
    const dataCorona = useSelector(selectDataCorona);
    const isFetching = useSelector(selectIsFetching);

    const filter = isFetching
        ? null
        : dataCorona.filter((data) =>
              data.name.toLowerCase().includes(countries)
          );
    return (
        <TableContainer>
            <SearchFieldWrap>
                <SearchFieldContainer
                    type="search"
                    placeholder="search country"
                    onChange={(e) => dispatch(filterCountries(e.target.value))}
                />
            </SearchFieldWrap>
            <WrapContainer>
                {filter.map((obj) => (
                    <CountryContainer>
                        <NameContainer>{obj.name}</NameContainer>
                        <CasesContainer>{obj.confirmed}</CasesContainer>
                    </CountryContainer>
                ))}
            </WrapContainer>
        </TableContainer>
    );
};

export default OtherCountries;
