import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import ReactMapGL, { Marker } from 'react-map-gl';

import {
	selectDataCorona,
	selectIsFetching
} from '../../redux/data/data.selectors';
import { selectToggleNumber } from '../../redux/toggle-hidden/toggle-hidden.selectors';

import { toggleNumber } from '../../redux/toggle-hidden/toggle-hidden.actions';
import { fetchDataStartAsync } from '../../redux/data/data.actions';

import { NumberContainer, CircleContainer } from '../mark/mark.style';

const Mark = () => {
	const [viewPort, setViewPort] = useState({
		latitude: 37.8,
		longitude: -122.4,
		zoom: 2,
		width: '100vw',
		height: '100vh'
	});
	const dataCorona = useSelector(selectDataCorona);
	const onToggleNumber = useSelector(selectToggleNumber);
	console.log(onToggleNumber);
	const dispatch = useDispatch();
	useEffect(() => {
		dispatch(fetchDataStartAsync());
	}, [dispatch]);
	return (
		<ReactMapGL
			{...viewPort}
			onViewportChange={setViewPort}
			mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_TOKEN}
			mapStyle='mapbox://styles/boyperfer/ck8c3jmqk2mjy1iqitxwkd1b6'
		>
			{dataCorona.map(({ coordinates, confirmed, name }, i) => (
				<Marker
					key={i}
					latitude={coordinates[0]}
					longitude={coordinates[1]}
				>
					<NumberContainer>
						{confirmed}
						{console.log(1)}
					</NumberContainer>
					<CircleContainer onClick={() => dispatch(toggleNumber())} />
				</Marker>
			))}
		</ReactMapGL>
	);
};

export default Mark;
