import styled, { css } from 'styled-components';

const getNumber = ({ onToggleNumber }) => {
	return onToggleNumber
		? css`
				visibility: visible;
		  `
		: css`
				visibility: visible;
		  `;
};

export const NumberContainer = styled.div`
	font-size: 1rem;
	color: white;
	${getNumber}
`;
