import styled, { css } from "styled-components";

import mainStyle from "../../styles/main";

const {
    variables: { greyDark4, textWhite, primary },
} = mainStyle;

const getNameCountry = ({ totalCases }) => {
    console.log(totalCases);
    totalCases
        ? css`
              font-size: 2rem;
          `
        : css`
              font-size: 5rem;
          `;
};

const getCountryContainer = ({}) => {
    totalCases
        ? css`
              flex-direction: column;
          `
        : css`
              flex-direction: row;
          `;
};

export const CountryContainer = styled.div`
    display: flex;
    ${getCountryContainer}
    justify-content: center;
    align-items: center;
    background-color: ${greyDark4};
    margin: 1rem 1rem 1rem 1rem;
`;

export const NameCountryContainer = styled.h1`
    color: ${textWhite};
    margin: 0;
    padding: 0 1rem;
    ${getNameCountry}
`;

export const ImgContainer = styled.img`
    width: 6rem;
    max-height: 6rem;
`;

export const TotalCasesContainer = styled.div`
    color: ${primary};
`;
