import React from "react";
import { useSelector, useDispatch } from "react-redux";

import {
    selectIsFetching,
    selectDataObjectCorona,
} from "../../redux/data/data.selectors";
import {
    selectToggleHome,
    selectDisplayHome,
    selectHome,
} from "../../redux/myhome/myhome.selectors";
import { changeMyHome } from "../../redux/myhome/myhome.actions";

import { mobileAndTabletcheck } from "../../assets/utils/utils";

import {
    HomeContainer,
    DetailContainer,
    FlagContainer,
    NavigationHomeContainer,
    DateContainer,
    OverallContainer,
} from "./myhome.styles";

const MyHome = () => {
    const isFetching = useSelector(selectIsFetching);
    const dataObjectCorona = useSelector(selectDataObjectCorona);
    const isOnHome = useSelector(selectToggleHome);
    const displayHome = useSelector(selectDisplayHome);
    const dispatch = useDispatch();
    const mobile = mobileAndTabletcheck();
    const home = useSelector(selectHome);
    Object.filter = (obj, predicate) =>
        Object.keys(obj)
            .filter((key) => predicate(obj[key]))
            .reduce((res, key) => ((res[key] = obj[key]), res), {});
    const filter = Object.filter(dataObjectCorona, (obj) =>
        obj.name.toLowerCase().includes(home.toLowerCase())
    );
    console.log(filter);
    return isFetching ? (
        <div />
    ) : mobile ? (
        displayHome ? (
            <div>
                <OverallContainer>
                    <HomeContainer isOnHome={isOnHome}>
                        <FlagContainer />
                        <DetailContainer />
                        <NavigationHomeContainer />
                        <DateContainer />
                    </HomeContainer>
                </OverallContainer>
            </div>
        ) : null
    ) : (
        // <HomeContainer>
        // 	<FlagContainer />
        // 	<DetailContainer />
        // 	<NavigationHomeContainer />
        // 	<DateContainer />
        // </HomeContainer>
        <div>
            <input
                type="search"
                placeholder="search country"
                onChange={(e) => dispatch(changeMyHome(e.target.value))}
            />
            <div>adfasd</div>
        </div>
    );
};

export default MyHome;
