import React from 'react';
import { Popup } from 'react-mapbox-gl';
import { useSelector } from 'react-redux';

import { selectPopupCountries } from '../../redux/popup/popup.selectors';

import './popup.styles.scss';
import {
	NumberContainer,
	NameContainer,
	CountryContainer
} from './popup.styles';

import CodeCountry from '../../assets/countryflag.json';

const PopupNumber = () => {
	const popupCountries = useSelector(selectPopupCountries);
	// const { Name, Code } = CodeCountry;
	const modifyNameCountry = name => name.replace(/ /g, '').toLowerCase();

	return popupCountries.map(
		({ confirmed, deaths, recovered, name, coordinates }, i) => {
			const getCode = CodeCountry.find(
				country =>
					modifyNameCountry(name) === modifyNameCountry(country.Name)
			);

			return (
				<Popup key={i} coordinates={[coordinates[1], coordinates[0]]}>
					<NameContainer>
						{name}{' '}
						{typeof getCode !== 'undefined' ? (
							<CountryContainer
								src={`https://lipis.github.io/flag-icon-css/flags/4x3/${getCode.Code.toLowerCase()}.svg`}
							/>
						) : (
							<div></div>
						)}
					</NameContainer>
					<NumberContainer> {confirmed} confirmed </NumberContainer>
					<NumberContainer> {deaths} deaths </NumberContainer>
					<NumberContainer> {recovered} recovered </NumberContainer>
				</Popup>
			);
		}
	);
};

export default PopupNumber;
