import styled from "styled-components";

import mainStyle from "../../styles/main";

const {
    variables: { white, black, greyDark4, greyDark1 },
    device: { mobile },
} = mainStyle;

export const DetailOthersContainer = styled.div`
    margin-left: 3rem;
    height: 100%;
    @media ${mobile} {
        bottom: 1rem;
        width: 100vw;
        margin: 0;
    }
`;

export const DetailOthersWrap = styled.div`
    position: absolute;
    top: 60vh;
    font-size: 3rem;
    color: ${greyDark1};
    /* background-color: ${greyDark4}; */
    background-color: ${white};
    z-index: 1000;
    @media ${mobile} {
        width: 100vw;
        height: 40vh;
    }
`;

export const NameContainer = styled.div`
    font-size: 3rem;
    text-align: center;
    background-color: ${greyDark1};
    color: ${white};
`;

export const ConfirmedContainer = styled.div`
    font-size: 3rem;
    border: 1px solid ${greyDark1};
    margin: 1rem 0;
`;
export const RecoveredContainer = styled.div`
    font-size: 3rem;
`;

export const DeathsContainer = styled.div`
    font-size: 3rem;
`;
