import styled, { css } from 'styled-components';
import { Layer, Popup } from 'react-mapbox-gl';

const getNumber = ({ onToggleNumber }) => {
	return onToggleNumber
		? css`
				visibility: hidden;
		  `
		: css`
				visibility: visible;
		  `;
};

export const NumberContainer = styled.div`
	font-size: 1rem;
	color: white;
	${getNumber}
`;

export const CircleContainer = styled(Layer)`
	height: 4rem;
	width: 4rem;
	border-radius: 10rem;
	background-color: red;
`;

export const PopupContainer = styled(Popup)`
	position: relative;
	border: 1px solid black;
	padding: 5px 5px;
	font-size: 20px;
`;
