import LanguageActionTypes from './language.types';

const INITIAL_STATE = {
	isVn: false,
};

const languageReducer = (state = INITIAL_STATE, action) => {
	switch (action.type) {
		case LanguageActionTypes.CHANGE_LANGUAGE:
			return {
				...state,
				vernacular: action.payload,
			};
		default:
			return state;
	}
};

export default languageReducer;
