import React from 'react';
import { Popup } from 'react-mapbox-gl';
import { useSelector } from 'react-redux';

import { selectPopupCountries } from '../../redux/popup/popup.selectors';

import './popup.styles.scss';
import { ConfirmedContainer, NameContainer } from './popup.styles';

const PopupNumber = () => {
	const popupCountries = useSelector(selectPopupCountries);

	return popupCountries.map(({ confirmed, name, coordinates }) => (
		<Popup coordinates={[coordinates[1], coordinates[0]]}>
			<NameContainer> {name} </NameContainer>
			<ConfirmedContainer> {confirmed} confirmed </ConfirmedContainer>
		</Popup>
	));
};

export default PopupNumber;
