import styled from 'styled-components';

import mainStyle from '../../styles/main';

const {
	variables: { primary, primaryLight, white, black },
} = mainStyle;

export const HomeContainer = styled.span`
	float: left;
	width: 250px;
	background: rgba(255, 255, 255, 1);
	text-align: center;
`;

export const CountryContainer = styled.span`
	height: 7rem;
	width: 7rem;
	border-radius: 50%;
	background-color: ${white};
	position: relative
	text-align: center;
	cursor: pointer;
	backface-visibility: hidden;
`;

export const NameContainer = styled.p`
	color: rgba(0, 0, 0, 0.4);
	margin: 10px 0;
	font-weight: 100;
	font-size: 0.75em;
`;

export const NumberContainer = styled.span`
	margin-bottom: 20px;
	padding-bottom: 10px;
	display: inline-block;
	width: 125px;
	font-size: 1em;
	font-weight: 700;
	letter-spacing: 1px;
	color: rgba(0, 0, 0, 0.5);
	border-bottom: 1px solid rgba(0, 0, 0, 0.1);
`;

export const TitleContainer = styled.h1`
	margin: 20px 0 10px 0;
	font-size: 1.25em;
	color: rgba(0, 0, 0, 0.8);
`;
